function displayByFMID(){
    console.log("\ndisplayByFMID");
    var search_info = document.getElementById("FMIDSearchText").value;
    var found = false;
    addTableHeader();
    for(i=0; i < marketName.length; i++){
        var value = FMID[i];
        if(value.search(search_info) != -1 ){
            found = true;
            HTML += "<tr>" 
            + "<td>" + FMID[i] + "</td>"
            + "<td>"  + "<a href = " + website[i] + "> "+ marketName[i] + "</a></td>"
            + "<td>" + city[i] + "</td>"
            + "<td>" + state[i] + "</td>"
            + "<td>" + zip[i] + "</td>"
            + "<td>" + website[i] + "</td>"
            + "</tr>";
        }
    }
    if(!found){
        HTML = "FMID not found"
    }
    displayInfo();

}


function displayAllMarkets(){
    console.log("displayAllMarkets");

    addTableHeader();
    for(i=0; i < marketName.length; i++){
        HTML += "<tr>" 
        + "<td>" + FMID[i] + "</td>"
        + "<td>"  + "<a href = " + website[i] + "> "+ marketName[i] + "</a></td>"
        + "<td>" + city[i] + "</td>"
        + "<td>" + state[i] + "</td>"
        + "<td>" + zip[i] + "</td>"
        + "<td>" + website[i] + "</td>"
        + "</tr>";
    }
}

function addTableHeader(){
        console.log("addTableHeader");

        HTML = "<thread>"
        + "<td> FMID </td>"
        + "<td> Market Name </td>"
        + "<td> City </td>"
        + "<td> State </td>"
        + "<td> Zip code </td>"
        + "<td> Website </td>"
        + "</thread>";
}

function displayInfo(){
    console.log("DISPLAYINFO");
    if(init_page){
        displayAllMarkets();
    }
    init_page = false;
    document.getElementById("displayInfo").innerHTML=HTML;

}

