function compare_piechart(name, data) {
document.getElementById("name1").innerHTML=name;
var sum = data.reduce(function(a, b){return a + b;}, 0);
result = [];
for(var i in data){
    result.push(Math.round(data[i]/sum * 10000) / 100);
}

var chart = new CanvasJS.Chart("chartContainer2", {
    theme: "light2",
	animationEnabled: true,
	title: {
		text: "Total " + name.charAt(0).toUpperCase() + name.slice(1) + " Value comparised by Meal"
	},
	data: [{
		type: "pie",
		indexLabelFontSize: 18,
		radius: 120,
		indexLabel: "{label} - {y}",
		yValueFormatString: "###0.0\"%\"",
		click: explodePie,
		dataPoints: [
			{ y: result[0], label: "Breakfast" },
			{ y: result[1], label: "Lunch"},
			{ y: result[2], label: "Dinner" }
		]
	}]
});
chart.render();

function explodePie(e) {
	for(var i = 0; i < e.dataSeries.dataPoints.length; i++) {
		if(i !== e.dataPointIndex)
			e.dataSeries.dataPoints[i].exploded = false;
	}
}
}

function nutrients_piechart(data) {
var sum = data.reduce(function(a, b){return a + b;}, 0);
result = [];
for(var i in data){
    result.push(Math.round(data[i]/sum * 10000) / 100);
}

var chart = new CanvasJS.Chart("chartContainer", {
	theme: "light2",
	animationEnabled: true,
	title: {
		text: "Total nutrients in Recipes"
	},
	data: [{
		type: "pie",
		indexLabelFontSize: 18,
		radius: 120,
		indexLabel: "{label} - {y}",
		yValueFormatString: "###0.0\"%\"",
		click: explodePie,
		dataPoints: [
			{ y: result[0], label: "Saturated Fat" },
			{ y: result[1], label: "Cholesterol"},
			{ y: result[2], label: "Fiber" },
			{ y: result[3], label: "Sodium" },
			{ y: result[4], label: "Sugar" }
		]
	}]
});
chart.render();

function explodePie(e) {
	for(var i = 0; i < e.dataSeries.dataPoints.length; i++) {
		if(i !== e.dataPointIndex)
			e.dataSeries.dataPoints[i].exploded = false;
	}
}
}

