var title = "{{title}}".split(",");
var id = "{{id}}".split(",");
var obj = "{{obj}}".split(",");
var cur = 1;
var titleSSelected = [];
var idSelected = [];
HTML = "";

for( i = 0; i < title.length; i++){
    title[i] = title[i].replace(/@/g, ",");
}
function prev(){
    cur -= 1;
    if(cur == 1){
        document.getElementById("prev").style="display:none;";
    }
    document.getElementById("next").style="display:block;";
    HTML = "";
    tableHeader();

    var start = (cur-1)*20;
    var num = start+1;
    if(start + 20 >= titleSSelected.length){
        for(i=start; i < title.length; i++){
            HTML += "<tr>" 
            + "<td>" + num.toString() + "</td>" 
            + "<td>"  + "<a href = /recipe/" + idSelected[i] + "> "+ titleSSelected[i] + "</a></td>"
            + "</tr>";
            num += 1;
        }
    }
    else{
        for(i=start; i < start+20; i++){
            HTML += "<tr>" 
            + "<td>" + num.toString() + "</td>" 
            + "<td>"  + "<a href = /recipe/" + idSelected[i] + "> "+ titleSSelected[i] + "</a></td>"
            + "</tr>";
            num += 1;
        }
    }
    document.getElementById("table").innerHTML=HTML;
}

function next(){

    cur += 1;
    if(cur*20 >= titleSSelected.length){
        document.getElementById("next").style="display:none;";
    }
    document.getElementById("prev").style="display:block;";
    HTML = "";
    tableHeader();

    var start = (cur-1)*20;
    var num = start+1;
    if(start + 20 >= titleSSelected.length){
        for(i=start; i < titleSSelected.length; i++){
            HTML += "<tr>" 
            + "<td>" + num.toString() + "</td>" 
            + "<td>"  + "<a href = /recipe/" + idSelected[i] + "> "+ titleSSelected[i] + "</a></td>"
            + "</tr>";
            num += 1;
        }
    }
    else{
        for(i=start; i < start+20; i++){
            HTML += "<tr>" 
            + "<td>" + num.toString() + "</td>" 
            + "<td>"  + "<a href = /recipe/" + idSelected[i] + "> "+ titleSSelected[i] + "</a></td>"
            + "</tr>";
            num += 1;
        }
    }
    document.getElementById("table").innerHTML=HTML;
}

function searchFunction(){

    titleSSelected= [];
    idSelected = [];
    HTML= "";
    tableHeader();
    var search_info = document.getElementById("recipeText").value.toLowerCase();
    const name = "title";
    var num = 1;
    for(i=0; i < title.length; i++){
        console.log(title[i] + " ----> " + id[i]);
        if(title[i] == "Myrnas Tuna Mouse"){
            // console.log(obj.keys(name));
            // console.log(obj[i][0].title);
            // console.log(obj[i][0][name]);

        }
        var value = title[i].toLowerCase();
        if(value.search(search_info) != -1 ){
            titleSSelected.push(title[i]);
            idSelected.push(id[i]);

        }
    }
    if(titleSSelected.length > 0 && titleSSelected.length <= 20){

        for(i=0; i<titleSSelected.length; i++){
            //console.log("-> " + idSelected[i]);
            HTML += "<tr>" 
            + "<td>" + num.toString() + "</td>" 
            + "<td>"  + "<a href = /recipe/" + idSelected[i] + "> "+ titleSSelected[i] + "</a></td>"
            + "</tr>";
            num += 1;
        }
        document.getElementById("table").style = "display: block;";
        document.getElementById("table").innerHTML=HTML;
        document.getElementById("prev").style="display:none";
        document.getElementById("next").style="display:none";
    }
    else if(titleSSelected.length <= 0){
        document.getElementById("table").style = "display: none;";
        document.getElementById("prev").style="display:none";
        document.getElementById("next").style="display:none";
    }
    else{
        num = 1;
        for(i=0; i<20; i++){
            //console.log(titleSSelected[i] + "-> " + idSelected[i]);
            HTML += "<tr>"  
            + "<td>" + num.toString() + "</td>" 
            + "<td>"  + "<a href = /recipe/" + idSelected[i] + "> "+ titleSSelected[i] + "</a></td>"
            + "</tr>";
            num += 1;
        }
        document.getElementById("table").style = "display: block;";
        document.getElementById("table").innerHTML=HTML;
        document.getElementById("next").style="display:block;";
    }
}

