var marketName = "{{marketName}}".split(",");
var city = "{{city}}".split(","); 
var state = "{{state}}".split(","); 
var zip = "{{zip}}".split(","); 
var FMID = "{{FMID}}".split(","); 
var website = "{{website}}".split(",");

var cur = 1;
var marketSearched= [];
var citySearched= [];
var stateSearched= [];
var zipSearched= [];
var FMIDSearched= [];
var websiteSearched= [];
var searchByCondition = "name";

HTML = "";

function prev(){
    cur -= 1;
    if(cur == 1){
        document.getElementById("prev").style="display:none;";
    }
    document.getElementById("next").style="display:block;";
    HTML = "";
    tableHeader();

    var start = (cur-1)*20;
    var num = start+1;
    if(start + 20 >= marketSearched.length){
        for(i=start; i < marketName.length; i++){
          HTML += "<tr>" 
          + "<td>" + FMIDSearched[i] + "</td>"
          + "<td>"  + "<a href = " + websiteSearched[i] + "> "+ marketSearched[i] + "</a></td>"
          + "<td>" + citySearched[i] + "</td>"
          + "<td>" + stateSearched[i] + "</td>"
          + "<td>" + zipSearched[i] + "</td>"
          + "<td>"  + "<a href = " + websiteSearched[i] + "> "+ websiteSearched[i] + "</a></td>"
          + "</tr>";
            num += 1;
        }
    }
    else{
        for(i=start; i < start+20; i++){
          HTML += "<tr>" 
          + "<td>" + FMIDSearched[i] + "</td>"
          + "<td>"  + "<a href = " + websiteSearched[i] + "> "+ marketSearched[i] + "</a></td>"
          + "<td>" + citySearched[i] + "</td>"
          + "<td>" + stateSearched[i] + "</td>"
          + "<td>" + zipSearched[i] + "</td>"
          + "<td>"  + "<a href = " + websiteSearched[i] + "> "+ websiteSearched[i] + "</a></td>"
          + "</tr>";
            num += 1;
        }
    }
    document.getElementById("table").innerHTML=HTML;
}

function next(){

    cur += 1;
    if(cur*20 >= marketSearched.length){
        document.getElementById("next").style="display:none;";
    }
    document.getElementById("prev").style="display:block;";
    HTML = "";
    tableHeader();

    var start = (cur-1)*20;
    var num = start+1;
    if(start + 20 >= marketSearched.length){
        for(i=start; i < marketSearched.length; i++){
          HTML += "<tr>" 
          + "<td>" + FMIDSearched[i] + "</td>"
          + "<td>"  + "<a href = " + websiteSearched[i] + "> "+ marketSearched[i] + "</a></td>"
          + "<td>" + citySearched[i] + "</td>"
