body {
  font-family: "Lato", sans-serif;
}

.center {
  margin-left: auto;
  margin-right: auto;
}

/* Style the header */
.header {
  background-color: #f44336;
  color: white;
  text-align: center;
  width: 100%;
  height: 14%;
  font-size: 62px;

}

.topright {
  position: absolute;
  padding-top: 10px;
  top: 8px;
  right: 16px;
  font-size: 18px;
}



.accountBtn {
    display: flex;
    justify-content: space-around;
    background-color: #f44336; /* Red */
    border: #f000000;
    color: white;
    padding: 15px 32px;
    text-align: center;
    text-decoration: none;
    display: inline-block;
    font-size: 16px;
    margin: 4px 2px;
    cursor: pointer;

}

/* On mouse-over */
.accountBtn:hover {
  color: #f1f1f1;
}

/* Clear floats after the header */
.header:after {
  content: "";
  display: table;
  clear: both;
}


input[type=text] {
  margin-left:40%;
  width: 200px;
  box-sizing: border-box;
  border: 2px solid #ccc;
  border-radius: 4px;
  font-size: 16px;
  background-color: white;
  background-position: 10px 10px; 
  background-repeat: no-repeat;
  padding: 12px 20px 12px 40px;
  -webkit-transition: width 0.4s ease-in-out;
  transition: width 0.4s ease-in-out;
}

input[type=text]:focus {
  width: 30%;
}


.searchBtn {
